// components/TaskList.js
import React from 'react';

const TaskList = ({ tasks, markTask, deleteTask }) => {
  return (
    <ul style={{ listStyleType: 'none', padding: 0 }}>
      {tasks.map((task) => (
        <li key={task.id} style={{ marginBottom: '10px', display: 'flex', alignItems: 'center' }}>
          <input
            type="checkbox"
            checked={task.completed}
            onChange={() => markTask(task.id)}
            style={{ marginRight: '10px' }}
          />
          <span style={{ textDecoration: task.completed ? 'line-through' : 'none', flex: 1 }}>
            {task.text}
          </span>
          <button onClick={() => deleteTask(task.id)} style={{ padding: '3px 10px' }}>Delete</button>
        </li>
      ))}
    </ul>
  );
};

export default TaskList;
