// components/TaskList.js
import React from 'react';

const TaskList = ({ tasks, markTask, deleteTask }) => {
  return (
    <table className="task-list">
      <thead>
        <tr>
          <th>Task</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>
        {tasks.map((task) => (
          <tr key={task.id} className={`task-item ${task.completed ? 'completed' : ''}`}>
            <td>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => markTask(task.id)}
                className="task-item__checkbox"
              />
              <span className="task-item__text">{task.text}</span>
            </td>
            <td>
              <button onClick={() => deleteTask(task.id)} className="task-item__delete">Delete</button>
              <button className="task-item__edit">Edit</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default TaskList;
